public class Palindrome {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Declaring the String
		String a= "madam";
		String reverse= " ";
		for(int i=a.length()-1;i>=0;i--)
		{
		reverse +=a.charAt(i);
		System.out.println(reverse);

	}

     }
   }
